INSERT INTO players (team_id, name, uniform_number, position, height, weight, birthdate, hometown, bio, episode) VALUES
(2, '青島 太一', 11, 'MF', 170, 63, '2001-04-17', '静岡県藤枝市', '立正大学出身。清水エスパルスユースを経て加入した若手MF。スピードと技術を兼ね備える。', 'J3降格の悔しさを胸に、再昇格を誓うコメントが話題に。'),
(2, '五十嵐 太陽', 10, 'MF', 172, 60, '2003-04-14', '神奈川県相模原市', '川崎フロンターレユース出身。攻撃的センスに優れ、得点にも絡める司令塔タイプ。', '「10ゴールに絡む」と宣言し、将来のエース候補として期待されている。'),
(1, '小川 敦也', 7, 'PG', 190, 85, '2002-06-24', '新潟県', '筑波大学出身の若手ポイントガード。大学時代にアシスト王を獲得し、視野の広さとパス精度が武器。', '大型PGとして注目され、ブレックスの将来を担う存在。'),
(1, '比江島 慎', 6, 'SG', 191, 88, '1989-08-11', '福岡県', '日本代表経験を持つベテランSG。冷静な判断と勝負強さが特徴。', 'チャンピオンシップMVP経験者で、チームを牽引するリーダー。'),
(4, '谷 順成', NULL, 'ロードレーサー', 171, 68, '1994-08-04', '岐阜県岐阜市', 'チームキャプテンとしてブリッツェンを牽引。スプリント力と安定した走りで知られる。', '偉大な先輩の後を継ぎ、リーダーとしてチームをまとめる姿勢が注目されている。'),
(4, 'フォン・チュンカイ', NULL, 'ロードレーサー', NULL, NULL, NULL, '台湾', '台湾出身の国際派ライダー。アジア圏での経験を活かし、チームに新たな風を吹き込む。', 'チームのグローバル化を象徴する存在として注目を集めている。'),
(5, '高岸 宏行', NULL, '投手（右投右打）', 188, 94, '1992-10-08', '愛媛県', '済美高校・東洋大学出身。芸人ティモンディとしても知られる二刀流プレイヤー。', '「やればできる！」の精神で野球とエンタメを両立する姿が話題に。'),
(5, '田代 優翔', 27, '捕手', NULL, NULL, NULL, NULL, '若手捕手としてチームに定着。堅実なリードと強肩が評価されている。', 'リーグ選抜にも選出され、将来の正捕手候補として期待。'),
(6, '原田 欽庸', 1, 'GK', 183, 79, '1986-05-17', '埼玉県', '豊富な経験を誇るベテランGK。安定感とリーダーシップで守備を支える。', '若手選手の精神的支柱としてチームの結束を支える存在。'),
(6, '鈴木 隆雅', 7, 'DF', NULL, NULL, NULL, NULL, '堅実な守備とビルドアップ能力を兼ね備えるDF。チームの要として活躍。', 'Jリーグ昇格を見据え、チームの守備力向上に貢献している。');
